                                "name": "AmbaActionGr",
                                "location": "Global",
                                "tags": {
                                    "_deployed_by_amba": true
                                  },
                                "properties": {
                                    "groupShortName": "AmbaActionGr",
                                    "enabled": true,
                                    "copy": [
                                        {
                                            "name": "emailReceivers",
                                            "count": "[[length(variables('varALZMonitorActionGroupEmail'))]",
                                            "mode": "serial",
